mapper.select:
select m.* from tb_sysmenu m,tb_rolemenu rm 
where m.id=rm.menu_id where rm.role_id= #{roleId,jdbcType=VARCHAR}
order by m.menu_order asc

     select 

     from tb_sysmenu where 1=1

	     and menu_name like '%' || #{menuName,jdbcType=VARCHAR} || '%'


	     and menu_type = #{menuType,jdbcType=VARCHAR} 


	     and created_at >= #{startTime,jdbcType=VARCHAR} 


	     and created_at <= #{endTime,jdbcType=VARCHAR} 

      order by id desc
