mapper.select:
select

from tb_sysuser
where login_name = #{loginName,jdbcType=VARCHAR} and login_pwd = #{loginPwd,jdbcType=VARCHAR}

     select 

     from tb_sysuser where 1=1

	     and role_id = #{roleId,jdbcType=VARCHAR} 


	     and status = #{status,jdbcType=VARCHAR} 


	     and login_name like '%' || #{loginName,jdbcType=VARCHAR} || '%'


	     and created_at >= #{startTime,jdbcType=VARCHAR} 


	     and created_at <= #{endTime,jdbcType=VARCHAR} 

      order by id desc

mapper.update:
update tb_sysuser
set is_delete = 1
where id = #{id,jdbcType=VARCHAR}
