$.fn.extend({
	uploadFile : function(url,callback,errCallback,params){
		var pms = params;
		if(null == pms){
			pms = {};
		}
		$.afterTipForFile($(this));
		tip = $('#file------tip');
		$(this).on('change',function(){
			var thisObj = this;
			$(this).upload({
				action:url,
				data:pms,
				oncomplete: function(data) {
					var mess = data.responseText;
					if(data.status == 200){
						if(null != callback && typeof callback == 'function'){
							callback.call(thisObj,mess);
						}
					}
					tip.text('');
		        },onprogress: function(e) {
		        	tip.html("上传中...");
		        },onerror:function(e){
		        	if(null != errCallback && typeof errCallback == 'function'){
		        		errCallback.call(thisObj,e);
		        	}
		        }
			});
		});
	}
});
$.extend({
	afterTipForFile : function(obj){
		obj.after('<label id="file------tip" style="color:blue;"></label>');
	}
});